import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [studentQuestion, setStudentQuestion] = useState('');
  const [professorResponse, setProfessorResponse] = useState('');

  const handleStudentQuestionChange = (e) => {
    setStudentQuestion(e.target.value);
  };

  const handleStudentQuestionSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/process_tasks', {
        student_question: studentQuestion,
      });
      const professorResponse = await axios.post('http://localhost:5000/process_student_question', response.data);
      setProfessorResponse(professorResponse.data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="App">
      <form onSubmit={handleStudentQuestionSubmit}>
        <input
          type="text"
          value={studentQuestion}
          onChange={handleStudentQuestionChange}
        />
        <button type="submit">Ask Professor</button>
      </form>
      {professorResponse && <p>{professorResponse}</p>}
    </div>
  );
}

export default App;
