import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  padding: 50px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  width: 50%;
`;

const Input = styled.input`
  margin-bottom: 20px;
  height: 35px;
  padding: 0 10px;
  font-size: 16px;
`;

const Button = styled.button`
  height: 35px;
  font-size: 16px;
`;

const Paragraph = styled.div`
  margin-top: 50px;
  text-align: left;
  width: 50%;
`;

const AnswerBox = styled.div`
  max-height: 300px;
  overflow-y: auto;
  padding: 1em;
  border: 1px solid #ddd;
  border-radius: 4px;
  margin-top: 1em;
`;

function App() {
  const [studentQuestion, setStudentQuestion] = useState('');
  const [evaluationAnswer, setEvaluationAnswer] = useState('');
  const [assistantResponse, setAssistantAnswer] = useState('');
  const [professorAnswer, setProfessorAnswer] = useState('');

  const handleStudentQuestionChange = (e) => {
    setStudentQuestion(e.target.value);
  };

  const handleStudentQuestionSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/ask', {
          question: studentQuestion,
      });
      setEvaluationAnswer(response.data.hat_evaluation);

      const assistantAnswerObject = JSON.parse(response.data.mentors_script_output);
      setAssistantAnswer(assistantAnswerObject);

      setProfessorAnswer(response.data.mentors_script_2_output);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const renderJson = (data, level = 0) => {
    return Object.entries(data).map(([key, value]) => {
      const indent = " ".repeat(level * 2);

      if (typeof value === 'object' && value !== null) {
        return (
          <React.Fragment key={key}>
            {`${indent}${key}:\n`}
            {renderJson(value, level + 1)}
            <br />
          </React.Fragment>
        );
      } else {
        return (
          <React.Fragment key={key}>
            {`${indent}${key}: ${value}\n`}
            <br />
          </React.Fragment>
        );
      }
    });
  };

  return (
    <Container>
      <Form onSubmit={handleStudentQuestionSubmit}>
        <Input
          type="text"
          value={studentQuestion}
          onChange={handleStudentQuestionChange}
          placeholder="Ask your question here..."
        />
        <Button type="submit">Ask Professor</Button>
      </Form>
      {evaluationAnswer && (
        <Paragraph>
         <strong>Evaluation Answer:</strong>
         <p>{evaluationAnswer}</p>
        </Paragraph>
      )}
      {assistantResponse && (
        <Paragraph>
          <strong>Assistant's Response:</strong>
          <pre>{renderJson(assistantResponse)}</pre>
        </Paragraph>
      )}
      {professorAnswer && (
        <Paragraph>
          <strong>Professor's Answer:</strong>
          <AnswerBox>
            {professorAnswer.split('\n').map((paragraph, i) => (
              <p key={i}>{paragraph}</p>
            ))}
          </AnswerBox>
        </Paragraph>
      )}
    </Container>
  );
}

export default App;
