import React, { useState } from 'react';
import axios from 'axios';
import { Button, Input, Card, Typography, Divider } from 'antd';

const { Title, Paragraph } = Typography;

function App() {
  const [studentQuestion, setStudentQuestion] = useState('');
  const [assistantResponse, setAssistantResponse] = useState('');
  const [professorAnswer, setProfessorAnswer] = useState('');

  const handleStudentQuestionChange = (e) => {
    setStudentQuestion(e.target.value);
  };

  const handleStudentQuestionSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/ask', {
        question: studentQuestion,
      });
      setAssistantResponse(response.data.assistantResponse);
      setProfessorAnswer(response.data.professorAnswer);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="App" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100vh', gap: '20px' }}>
      <Title>Mentor Chat App</Title>
      <Card style={{ width: '80%' }}>
        <form onSubmit={handleStudentQuestionSubmit}>
          <Input
            placeholder="Ask your question here..."
            value={studentQuestion}
            onChange={handleStudentQuestionChange}
          />
          <Button type="primary" htmlType="submit" style={{ marginTop: '20px' }}>
            Ask Professor
          </Button>
        </form>
      </Card>
      {(assistantResponse || professorAnswer) && (
        <Card style={{ width: '80%' }}>
          <Title level={3}>Response:</Title>
          <Paragraph><strong>Assistant's Response:</strong> {assistantResponse}</Paragraph>
          <Divider />
          <Paragraph><strong>Professor's Answer:</strong> {professorAnswer}</Paragraph>
        </Card>
      )}
    </div>
  );
}

export default App;
