import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  padding: 50px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  width: 50%;
`;

const Input = styled.input`
  margin-bottom: 20px;
  height: 35px;
  padding: 0 10px;
  font-size: 16px;
`;

const Button = styled.button`
  height: 35px;
  font-size: 16px;
`;

const Paragraph = styled.p`
  margin-top: 50px;
  text-align: left;
  width: 50%;
`;

function App() {
  const [studentQuestion, setStudentQuestion] = useState('');
  const [response, setResponse] = useState({});

  const handleStudentQuestionChange = (e) => {
    setStudentQuestion(e.target.value);
  };

  const handleStudentQuestionSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/v1/ask', {
        student_question: studentQuestion,
      });

      setResponse(res.data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <Container>
      <Form onSubmit={handleStudentQuestionSubmit}>
        <Input
          type="text"
          value={studentQuestion}
          onChange={handleStudentQuestionChange}
          placeholder="Ask your question here..."
        />
        <Button type="submit">Ask</Button>
      </Form>

      {response && (
        <div>
          <Paragraph><strong>HAT Evaluation:</strong> {response.hat_evaluation}</Paragraph>

          {response.mentors_script_output && (
            <Paragraph><strong>Mentor Script Output:</strong> {response.mentors_script_output}</Paragraph>
          )}

          {response.mentors_script_2_output && (
            <Paragraph><strong>Mentor Script 2 Output:</strong> {response.mentors_script_2_output}</Paragraph>
          )}
        </div>
      )}
    </Container>
  );
}

export default App;
