import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [studentQuestion, setStudentQuestion] = useState('');
  const [assistantResponse, setAssistantResponse] = useState('');
  const [professorResponse, setProfessorResponse] = useState('');

  const handleStudentQuestionChange = (e) => {
    setStudentQuestion(e.target.value);
  };

  const handleStudentQuestionSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/ask', { question: studentQuestion });
      setAssistantResponse(response.data.assistantResponse);
      setProfessorResponse(response.data.professorAnswer);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="App">
      <form onSubmit={handleStudentQuestionSubmit}>
        <input type="text" value={studentQuestion} onChange={handleStudentQuestionChange} />
        <button type="submit">Ask Professor</button>
      </form>
      {assistantResponse && <p>{assistantResponse}</p>}
      {professorResponse && <p>{professorResponse}</p>}
    </div>
  );
}

export default App;
